        // JavaScript code for interacting with the editor and suggestions

        // Function to handle user selection of suggestion
        function insertSuggestion(suggestion) {
            // Insert the selected suggestion into the code editor at the cursor position
            const codeEditor = document.getElementById('code-editor');
            const cursorPosition = codeEditor.selectionStart;
            const code = codeEditor.value;
            const beforeCursor = code.slice(0, cursorPosition);
            const afterCursor = code.slice(cursorPosition);
            codeEditor.value = beforeCursor + suggestion + afterCursor;
            // Move cursor to end of inserted suggestion
            codeEditor.selectionStart = codeEditor.selectionEnd = beforeCursor.length + suggestion.length;
            codeEditor.focus();
        }

        // Function to update suggestions based on user input
        function updateSuggestions() {
            // Retrieve the last line typed by the user
            const codeEditor = document.getElementById('code-editor');
            const lines = codeEditor.value.split('\n');
            const lastLine = lines[lines.length - 1];

            // Extract the last four words from the last line
            const words = lastLine.split(' ');
            const lastFourWords = words.slice(-4).join(' ');

            // AJAX request to server to fetch suggestions based on last four words
            const xhr = new XMLHttpRequest();
            xhr.open('POST', '/suggest', true);
            xhr.setRequestHeader('Content-Type', 'application/json');
            xhr.onreadystatechange = function () {
                if (xhr.readyState === 4 && xhr.status === 200) {
                    // Parse response JSON and display suggestions
                    const suggestions = JSON.parse(xhr.responseText);
                    const suggestionsContainer = document.getElementById('suggestions');
                    suggestionsContainer.innerHTML = '';
                    suggestions.forEach(suggestion => {
                        const suggestionNode = document.createElement('div');
                        suggestionNode.textContent = suggestion;
                        suggestionNode.classList.add('suggestion');
                        suggestionNode.addEventListener('click', () => {
                            insertSuggestion(suggestion);
                        });
                        suggestionsContainer.appendChild(suggestionNode);
                    });
                }
            };
            xhr.send(JSON.stringify({ lastFourWords }));
        }

        // Event listener to update suggestions on code editor input
        const codeEditor = document.getElementById('code-editor');
        codeEditor.addEventListener('input', updateSuggestions);
    </script>
